package com.ce.game.myapplication.actimport android.graphics.Bitmapimport android.graphics.Canvasimport android.os.Bundleimport android.support.v7.app.AppCompatActivityimport android.widget.ImageViewimport android.widget.TextViewimport com.ce.game.myapplication.Rclass KotlinActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_kotlin)//        val view = findViewById(R.id.image_view_test) as TextView//        with(view) {//            setDrawingCacheEnabled(true)////            buildDrawingCache()//        }////        var bm = view.getDrawingCache()//        bm = null    }    override fun onWindowFocusChanged(hasFocus: Boolean) {        super.onWindowFocusChanged(hasFocus)        if (hasFocus) {            var bitmap: Bitmap            val view = findViewById(R.id.image_view_test) as TextView            bitmap = Bitmap.createBitmap(view.getWidth(), view.getHeight(),                    Bitmap.Config.ARGB_8888)            val canvas = Canvas(bitmap)            view.draw(canvas)            var i = 0            i += 1            val image = findViewById(R.id.target_view) as ImageView            with(image){                setImageBitmap(bitmap)            }        }    }}